type FunctionType = (...args: any[]) => any;

type ExtractFunction<T> = T extends FunctionType ? (...args: Parameters<T>) => ReturnType<T> : never;

/**
 * Extracts a typesafe function type from a union of function and non-function types.
 */
export function isFunction<T extends FunctionType>(value: T | unknown): value is ExtractFunction<T> {
  return typeof value === 'function';
}
