import { useRef } from 'react';

/**
 * Returns a constant value that does not change during the lifetime of the component.
 * The value can be a function, in which case it will be called once on initial render.
 */
export function useConstant<const T>(value: T | (() => T)): T {
  const res = useRef<[T]>(undefined);
  if (!res.current) res.current = [typeof value === 'function' ? (value as () => T)() : value] as const;
  return res.current[0];
}
