import { useRef } from 'react';

/**
 * Lazily creates a value once and reuses it for the component's lifetime.
 * Prefer this over `useMemo(() => value, [])` when the factory should only ever run once.
 */
export function useConstant<T>(factory: () => T): T {
  const ref = useRef<{ v: T }>();

  if (ref.current === undefined) {
    ref.current = { v: factory() };
  }

  return ref.current.v;
}
